import classNames from "classnames";
import { useState, useRef } from "react";
import { InputCheckboxComponent } from "./types";

export const InputCheckbox: InputCheckboxComponent = ({ id, checked: defaultChecked = false, disabled, onChange }) => {
  const { current: inputId } = useRef(`RampInputCheckbox-${id}`);
  const [checked, setChecked] = useState(defaultChecked);

  const handleChange = () => {
    const newChecked = !checked; // Toggle the checked state
    setChecked(newChecked); // Update the checked state
    if (onChange) {
      onChange(newChecked); // Pass the newChecked state to the onChange callback
    }
    console.log(`Checkbox ${inputId} clicked. New checked state: ${newChecked}`);
  };

  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>
      <label
        className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": checked,
          "RampInputCheckbox--label-disabled": disabled,
        })}
      />
      <input
        id={inputId}
        type="checkbox"
        className="RampInputCheckbox--label"
        checked={checked}
        disabled={disabled}
        onChange={handleChange}
      />
    </div>
  );
};
